SELECT USER_ID,UU.NICKNAME,UU.CITY_NAME AS 전체주소,UU.TLNO FROM USED_GOODS_BOARD UB LEFT JOIN (SELECT USER_ID,NICKNAME,CONCAT(CITY, ' ', STREET_ADDRESS1, ' ',STREET_ADDRESS2) AS CITY_NAME,CONCAT(substr(TLNO,1,3), '-', substr(TLNO,4,4), '-', substr(TLNO,8,4)) AS TLNO FROM USED_GOODS_USER) AS UU ON UB.WRITER_ID = UU.USER_ID GROUP BY USER_ID HAVING COUNT(user_id) >= 3 ORDER BY USER_ID DESC;